import React, { useState } from 'react';
import { AuthService } from '../../services/apiService.js';
import './AuthCommon.css';
import './Register.css';    // Estilos espec√≠ficos

const Register = ({ onRegisterSuccess, onGoToLogin }) => {
    const [formData, setFormData] = useState({
        nombre: '',
        correo: '',
        contrasena: '',
        confirmarContrasena: '',
        rol: 'estudiante'
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('üîç 1. Formulario registro enviado');
        console.log('üîç 2. Datos registro:', formData);
        setLoading(true);
        setError('');
        setSuccess('');


        if (formData.contrasena.length < 6) {
            setError('La contrase√±a debe tener al menos 6 caracteres');
            setLoading(false);
            return;
        }

        try {
            console.log('üîç 3. Llamando AuthService.registrar...');
            const resultado = await AuthService.registrar({
                nombre: formData.nombre,
                correo: formData.correo,
                contrasena: formData.contrasena,
                rol: formData.rol
            });
            console.log('üîç 4. Respuesta backend registro:', resultado);
            
            if (resultado.error) {
                setError(resultado.error);
            } else {
                setSuccess('¬°Registro exitoso! Redirigiendo al login...');
                // Opcional: redirigir autom√°ticamente despu√©s de 2 segundos
                console.log('üîç 5. Registro exitoso');
                setTimeout(() => {
                    onGoToLogin();
                }, 2000);
            }
        } catch (error) {
            console.log('üîç 6. Error catch:', error);
            setError('Error de conexi√≥n con el servidor');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="formulario">
        <h1>Registro</h1>

        <form onSubmit={handleSubmit}>
            <div className="input-group">
                <input 
                    type="text" 
                    name="nombre" 
                    value={formData.nombre}
                    onChange={handleChange}
                    required 
                />
                <label>Nombre Completo</label>
                <span></span>
            </div>

            <div className="input-group">
                <input 
                    type="email" 
                    name="correo" 
                    value={formData.correo}
                    onChange={handleChange}
                    required 
                 />
                 <label>Correo Electr√≥nico</label>
                <span></span>
            </div>

            <div className="input-group">
                <input type="password" name="contrase√±a" required />
                 <label>Contrase√±a</label>
                <span></span>
            </div>

            <div className="select-group">
                <label>Profesi√≥n</label>
                <select value={formData.rol} onChange={handleChange} name="rol">
                    <option value="estudiante">Estudiante</option>
                    <option value="profesor">Profesor</option>
                </select>
            </div>

            <input type="submit" value="Registrarme" />

            <div className="registrarse">
                ¬øYa tienes cuenta? <a href="index.html">Inicia sesi√≥n</a>
            </div>
        </form>
    </div>
    );
};

export default Register;
